<script>

    const footer = {
        data() {
            return {
                masters: [],
                categories: [],

                theme: localStorage.getItem('theme') || 'light'

            }
        },

        methods: {
            async getMasters() {
                const response = await fetch('{{route('getAllMasters')}}');
                this.masters = await response.json();
            },
            async getCategories() {
                const response = await fetch('{{route('getCategories')}}');
                this.categories = await response.json();
            },

            viewMaster(id) {
                window.location.href = `/masters/${id}`;
            },

            viewServices(id) {
                window.location.href = `/services/filter/${id}`;
            },

        },

        computed: {
            light_theme() {
                return this.theme === 'light';
            }
        },

        created() {
            window.addEventListener('theme-changed', (event) => {
                this.theme = event.detail;
            });
        },

        mounted() {
            this.getMasters();
            this.getCategories();
        }
    }

    Vue.createApp(footer).mount('#Footer');
</script>
